  <section class="content">          <div class="row">            <!-- left column -->           <?php $q = mysqli_query($link_music,"SELECT * FROM table_slider ORDER BY slider_id ASC");$total = mysqli_num_rows(mysqli_query($link_music,"SELECT slider_id  FROM table_slider"));if (!$_POST['submit']) {    ?> <form method="post">        <?php  $i = 1;        while ($r = mysqli_fetch_array($q)) {         ?>		 <div class="col-md-6">		<div class="box box-info">                <div class="box-header with-border">                  <h3 class="box-title">Quản Lý Slider Home <?=$i?></h3>                  <div class="box-tools pull-right">                    <button class="btn btn-box-tool" data-widget="collapse"><i class="fa fa-minus"></i></button>                    <button class="btn btn-box-tool" data-widget="remove"><i class="fa fa-times"></i></button>                  </div>                </div><!-- /.box-header --><div class="box-body">		<div class="form-group has-success">                      <label class="control-label" for="inputSuccess"><i class="fa fa-check"></i> Tên Silder <?=$i?></label>                      <input type="text" name="slider_name_[<?=$r['slider_id']?>]" class="form-control" id="inputSuccess" value="<?=$r['slider_name']?>" placeholder="Tên Slider  ..." />                    </div><div class="form-group has-error">                      <label class="control-label" for="inputSuccess"><i class="fa fa-chain"></i> LINK Silder <?=$i?></label>                      <input type="text" name="slider_link_[<?=$r['slider_id']?>]" placeholder="Nhập Link đường dẫn ..." value="<?=$r['slider_link']?>" class="form-control" id="inputSuccess" />                    </div><div class="form-group has-warning">                      <label class="control-label" for="inputSuccess"><i class="fa fa-picture-o"></i> IMG Silder <?=$i?></label>                      <input type="text" name="slider_img_[<?=$r['slider_id']?>]" placeholder="Nhập Link Ảnh hiển thị ..." value="<?=$r['slider_img']?>" class="form-control" id="inputSuccess" />                    </div> </div> </div> </div><!-- /.col-->		<?php  $i++; } ?><center>        <label class="control-label" for="inputSuccess"><i class="fa fa-check"></i> Tổng Số Slider </label>		<input size=4 name="slider_total" value="<?=$total?>" onClick="this.select()">		<td class=fr><input class="btn btn-primary" type="submit" value="ĐỒNG Ý" name=submit ></td></center></form>            </div><!-- ./row -->        </section><!-- /.content -->    <?php     }    else {        $i = 1;            while ($r = mysqli_fetch_array($q)) {            $id[$i] = $r['slider_id'];            $i++;            if ($r['slider_name'] != $slider_img_[$r['slider_id']]) {                mysqli_query($link_music,"UPDATE table_slider SET slider_name = '".$slider_name_[$r['slider_id']]."' WHERE slider_id IN (".$r['slider_id'].")");            }            if ($r['slider_link'] != $slider_link_[$r['slider_id']]) {                mysqli_query($link_music,"UPDATE table_slider SET slider_link = '".$slider_link_[$r['slider_id']]."' WHERE slider_id IN (".$r['slider_id'].")");            }            if ($r['slider_img'] != $slider_img_[$r['slider_id']]) {                mysqli_query($link_music,"UPDATE table_slider SET slider_img = '".$slider_img_[$r['slider_id']]."' WHERE slider_id IN (".$r['slider_id'].")");            }		}        if ($slider_total > $total) {            for ($i=$total+1; $i<=$slider_total; $i++) {                mysqli_query($link_music,"INSERT INTO table_slider (slider_name, slider_link, slider_img) VALUES ('','','')");            }        }        elseif ($slider_total < $total) {            for ($i=$slider_total+1; $i<=$total; $i++) {                natsort($id);                mysqli_query($link_music,"DELETE FROM table_slider WHERE slider_id IN (".$id[$i].")");            }        }        echo "Đã sửa xong !<meta http-equiv='refresh' content='0;url=$link'>";    }?> 